package twitterapp.src;import java.util.Scanner;import twitter4j.Twitter;import twitter4j.TwitterException;import twitter4j.TwitterFactory;public class Project1 {    public Project1() {    }    public static void main(String[] var0) {        Twitter twitter = TwitterFactory.getSingleton();        System.out.println("Enter your tweet: ");        Scanner sc = (new Scanner(System.in)).useDelimiter("\\n");        String message = var2.nextLine();        if (message.length() > 280) {            System.out.println("Tweet too long.");        }        try {            twitter.updateStatus(message);        } catch (TwitterException te) {            te.printStackTrace();        }    }}